def isStrangePlus(n):
    '''True all consecutive decimal digit
       pairs in n have prime sums.
    '''
    def test(a, b):
        return a + b in [2, 3, 5, 7, 11, 13, 17]

    xs = digits(n)
    return all(map(test, xs, xs[1:]))


