from itertools import (product)


# sumTwo :: [Int] -> Int -> [(Int, Int)]
def sumTwo(xs):
    '''All the pairs of integers in xs which
       sum to n.
    '''
    def go(n):
        ixs = list(enumerate(xs))
        return [
            (fst(x), fst(y)) for (x, y) in (
                product(ixs, ixs[1:])
            ) if fst(x) < fst(y) and n == snd(x) + snd(y)
        ]
    return lambda n: go(n)


