import java.util.ArrayList;
import java.util.List;

public class MonkeyCoconuts {

    public static void main(String[] args) {
        for (int sailors : new int[]{5, 6}) {
            List<Object[]> wakeStats = monkeyCoconuts(sailors);
            int nuts = (int) wakeStats.get(0)[0];
            System.out.printf("For %d sailors the initial nut count is %d%n", sailors, nuts);
            System.out.println("On each waking, the nut count, portion taken, and monkeys share are:");
            for (Object[] wake : wakeStats) {
                System.out.printf(" %d, %d, %d%n", wake[0], wake[1], wake[2]);
            }
            System.out.println();
        }
    }

    private static List<Object[]> monkeyCoconuts(int sailors) {
        int nuts = sailors;
        List<Object[]> wakeStats = new ArrayList<>();
        while (true) {
            int n0 = nuts;
            List<Integer> wakes = new ArrayList<>();
            for (int sailor = 0; sailor <= sailors; sailor++) {
                int portion = n0 / sailors;
                int remainder = n0 % sailors;
                wakes.add(n0);
                wakes.add(portion);
                wakes.add(remainder);
                if (portion <= 0 || remainder != (sailor != sailors ? 1 : 0)) {
                    nuts++;
                    break;
                }
                n0 = n0 - portion - remainder;
            }
            if (n0 == nuts) {
                break;
            }
            Object[] wakeArray = wakes.toArray();
            wakeStats.add(wakeArray);
        }
        Object[] nutsArray = new Object[]{nuts};
        wakeStats.add(0, nutsArray);
        return wakeStats;
    }

}

