package main

import (
	"errors"
	"fmt"
)

var seq = []int{0, 1, 1}

func q(n int) (int, error) {
	if n < 1 || n != int(n) {
		return 0, errors.New("n must be an int >= 1")
	}
	if len(seq) > n {
		return seq[n], nil
	}
	for i := len(seq); i <= n; i++ {
		ans, err1 := q(i - q(i-1))
		if err1 != nil {
			return 0, err1
		}
		ans2, err2 := q(i - q(i-2))
		if err2 != nil {
			return 0, err2
		}
		seq = append(seq, ans+ans2)
	}
	return seq[n], nil
}

func main() {
	first10 := make([]int, 10)
	for i := 1; i <= 10; i++ {
		val, err := q(i)
		if err != nil {
			fmt.Println("Q() value error(s)")
			return
		}
		first10[i-1] = val
	}
	expected := []int{1, 1, 2, 3, 3, 4, 5, 5, 6, 6}
	for i := range first10 {
		if first10[i] != expected[i] {
			fmt.Println("Q() value error(s)")
			return
		}
	}
