vector<BigInteger> cumu(int n) {
    vector<vector<BigInteger>> cache;
    cache.push_back({BigInteger(1)});

    for (int L = cache.size(); L < n + 1; L++) {
        vector<BigInteger> r;
        r.push_back(BigInteger(0));
        for (int x = 1; x < L + 1; x++)
            r.push_back(r.back() + cache[L - x][min(x, L - x)]);
