public class Shoelace {

    public static void main(String[] args) {
        List<Pair<Double, Double>> points = new ArrayList<>();
        points.add(new Pair<>(3.0, 4.0));
        points.add(new Pair<>(5.0, 11.0));
        points.add(new Pair<>(12.0, 8.0));
        points.add(new Pair<>(9.0, 5.0));
        points.add(new Pair<>(5.0, 6.0));

        double ans = shoelace(points);
        System.out.println(ans);
    }

    private static double shoelace(List<Pair<Double, Double>> points) {
        double leftSum = 0.0;
        double rightSum = 0.0;

        for (int i = 0; i < points.size(); ++i) {
            int j = (i + 1) % points.size();
            leftSum += points.get(i).getKey() * points.get(j).getValue();
            rightSum += points.get(j).getKey() * points.get(i).getValue();
        }

        return 0.5 * Math.abs(leftSum - rightSum);
    }

    private static class Pair<K, V> {
        private final K key;
        private final V value;

        public Pair(K key, V value) {
            this.key = key;
            this.value = value;
        }

        public K getKey() {
            return key;
        }

        public V getValue() {
            return value;
        }
    }
