bool IsSelfDescribing(int n) {
    bool isSelfDescribing = false;
    unordered_map<int, int> digit;
    string str = to_string(n);
    for (int i = 0; i < str.length(); i++) {
        int k = str[i] - '0';
        if (digit.find(k) != digit.end()) {
            digit[k]++;
        } else {
            digit[k] = 1;
        }
    }
    int c = 0;
    for (int j = 0; j < str.length(); j++) {
        int l = str[j] - '0';
        if (digit.find(j) != digit.end()) {
            if (digit[j] == l) {
                c++;
            }
        } else if (l == 0) {
            c++;
        } else {
            break;
        }
    }
    if (c == str.length()) {
        isSelfDescribing = true;
    }
    return isSelfDescribing;
}

